mapper.select:
select status
from enroll
where member_no = #{member_no}
and
study_no=#{no}

mapper.update:
update enroll
set status='진행중'
where study_no=#{study_no}
	and member_no=#{member_no}

update enroll
set status='탈락'
where study_no=#{study_no}
	and member_no=#{member_no}

update enroll
set status='무단이탈', skip=1
where study_no=#{study_no}
	and member_no=#{member_no}
